extensionAttribute.scriptContentsMac:
#!/bin/sh
warning=`defaults read /Library/MonitoringClient/ClientData/UnifiedStatus CurrentWarning`
if [ -f /Library/MonitoringClient/Utilities/ExportStatus ]; then
	if [ "$warning" ];
		then echo "Has issue";
		else echo "No problems detected";
	fi
else echo "Watchman Monitoring not installed"
fi
